package leetcode

import (
	"github.com/stretchr/testify/assert"
	"testing"
)

func Test_findAllSubstring(t *testing.T) {
	res := findAllSubstring("abac")
	assert.Equal(t, []string{"a", "b", "ba", "bac", "ab", "a", "ac", "aba", "c", "abac"}, res)
}

func Test_isPalindromeString(t *testing.T) {
	isP := isPalindromeString("aabbaa")
	assert.True(t, isP)
}

func Test_longestPalindrome(t *testing.T) {
	ls := longestPalindromeBrutalForce("abac")
	assert.Equal(t, "aba", ls)
}

func Test_longestPalindrome1(t *testing.T) {
	ls := longestPalindromeBrutalForce("abcdbbfcba")
	assert.Equal(t, "bb", ls)
}

func Test_longestPalindrome2(t *testing.T) {
	ls := longestPalindromeBrutalForce("")
	assert.Equal(t, "", ls)
}
